	@Override
	public void onTestFailure(ITestResult result) {
		// TODO Auto-generated method stub
		System.out.println("Failed Testcases.....");
		TestUtils utils=new TestUtils();
		try {
		utils.failed(result.getMethod().getMethodName());
		}catch(Exception e)
		{
			e.printStackTrace();
		}
		
	}

